package JavaCode;

public class Calculator
{
	
	public Calculator() {
		super();
		// TODO Auto-generated constructor stub
	}
	public int  add(int a , int b) 
	{
		
		return a+b;
		
	}
	public int  sub(int a, int b)
	{
		return a-b;
	}
	public int  mul(int a, int b)
	{
		
		return a*b;
		
	}
	public double div(int a, int b)
	{
	
		return a/b;
	}
	

}
